// src/StudentSearch.js
import React, { useState } from 'react';
import '../App.css';
import baseUrl from '../config/baseUrl'

const StudentSearch = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);

    const handleSearch = async () => {
        const requestOptions = {
            method: "GET",
            redirect: "follow"
          };
          
          fetch(`${baseUrl}/student/searchstudent/${searchTerm}`, requestOptions)
              .then((response) =>  response.json())
              .then((result) => {
                  console.log(result)
                  setResults()
              })
            .catch((error) => console.error(error));
    };
    
    

    return (
        <div>

            <div className="heading-container">
                <h1>Student Finder</h1>
            </div>

            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search students by name"
                    value={searchTerm}
                    onChange={e => setSearchTerm(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>
            <ul>
                {results.map(student => (
                    <li key={student.id}>
                        <div className="student-info">
                            <span className="student-name">{student.name}</span>
                            <span className="student-details">Age: {student.age}</span>
                            <span className="student-details">Major: {student.major}</span>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default StudentSearch;
