import React, { useState, useEffect } from 'react';
import '../App.css';
import { baseUrl } from '../config/baseUrl'

const StudentSearch = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);

   
    const fetchAllStudents = async () => {
        

        try {
            const response = await fetch(`${baseUrl}/student/searchstudent`);
            const data = await response.json();
            setResults(data.students);
        } catch (error) {
            console.error("Error fetching students:", error);
        }
    };

    
    const handleSearch = async () => {
        if (!searchTerm) {
            fetchAllStudents();
            return;
        }

        const requestOptions = {
            method: "GET",
            redirect: "follow"
          };
          
          fetch(`${baseUrl}/student/searchstudent/${searchTerm}`, requestOptions)
            .then((response) => response.json())
              .then((result) => {
                  console.log(result)
                  setResults(result.students)
              })
            .catch((error) => console.error(error));
    };

  
    useEffect(() => {
        fetchAllStudents();
    }, []);

    return (
        <div>
            <div className="heading-container">
                <h1>Student Finder</h1>
            </div>
        
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search students by name"
                    value={searchTerm}
                    onChange={e => setSearchTerm(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>
        
            <div>
    {results.length > 0 ? (
        <ul>
            {results.map(student => (
                <li key={student._id}>
                    <div className="student-info">
                        <span className="student-name">{student.name}</span>
                        <span className="student-details">Email: {student.email}</span>
                        <span className="student-details">Standard: {student.std}</span>
                        <span className="student-details">Division: {student.division}</span>
                    </div>
                </li>
            ))}
        </ul>
    ) : (
        <div className="empty-message">
            No students found.
        </div>
    )}
</div>

        </div>
    );
};

export default StudentSearch;
