import React, { useState, useEffect } from 'react';
import '../App.css';
import { baseUrl } from '../config/baseUrl'

const StudentSearch = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);

    // Function to fetch all students
    const fetchAllStudents = async () => {
        const requestOptions = {
            method: "GET",
            redirect: "follow"
        };

        try {
            const response = await fetch(`${baseUrl}/student`);
            const data = await response.json();
            setResults(data.students);
        } catch (error) {
            console.error("Error fetching students:", error);
        }
    };

    // Function to handle search
    const handleSearch = async () => {
        if (!searchTerm) {
            // If search term is empty, fetch all students
            fetchAllStudents();
            return;
        }

        const requestOptions = {
            method: "GET",
            redirect: "follow"
        };

        try {
            const response = await fetch(`${baseUrl}/student/searchstudent/${searchTerm}`, requestOptions);
            const data = await response.json();
            setResults(data.students);
        } catch (error) {
            console.error("Error searching students:", error);
            setResults([]); // Clear results in case of error
        }
    };

    // Fetch all students when component mounts
    useEffect(() => {
        fetchAllStudents();
    }, []);

    return (
        <div>
            <div className="heading-container">
                <h1>Student Finder</h1>
            </div>
        
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search students by name"
                    value={searchTerm}
                    onChange={e => setSearchTerm(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>
        
            <div>
                <ul>
                    {results.map(student => (
                        <li key={student._id}>
                            <div className="student-info">
                                <span className="student-name">{student.name}</span>
                                <span className="student-details">Email: {student.email}</span>
                                <span className="student-details">Standard: {student.std}</span>
                                <span className="student-details">Division: {student.division}</span>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default StudentSearch;
