// src/StudentSearch.js
import React, { useState } from 'react';

const students = [
  { id: 1, name: 'John Doe', age: 20, major: 'Computer Science' },
  { id: 2, name: 'Jane Smith', age: 22, major: 'Biology' },
  { id: 3, name: 'Michael Johnson', age: 21, major: 'Mathematics' },
  { id: 4, name: 'Emily Davis', age: 23, major: 'Physics' },
  { id: 5, name: 'William Brown', age: 24, major: 'Chemistry' },
];

const StudentSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    const filteredResults = students.filter(student =>
      student.name.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setResults(filteredResults);
  };

  return (
    <div>
      <h1>Student Search</h1>
      <input
        type="text"
        placeholder="Search students by name"
        value={searchTerm}
        onChange={handleSearch}
      />
      <ul>
        {results.map(student => (
          <li key={student.id}>
            {student.name} - {student.age} - {student.major}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentSearch;
